document.addEventListener('builder:carousel:load', function (event) {
    window.builderCarousel = event.detail.carousel;
});

document.addEventListener('builder:carousel:change', function (event) {
    var index = event.detail.slide;
    if (state.activeSlide !== index) {
        update(function (newState) {
            newState.activeSlide = index;
        })
    }
});

actions.click:
update(function (newState) {
    const index = state.product.images.indexOf(state.imagesItem);
    newState.activeSlide = index
    builderCarousel.slickGoTo(index)
})

actions.submit:
var _product = state.product;
event.preventDefault();

// TODO: support radio
var options = [].slice.call(event.currentTarget.querySelectorAll('input,select,textarea'))
    .filter(function (input) { return input.name && (input.name[0].toUpperCase() === input.name[0]) })
    .filter(function (input) { return input.type === 'radio' ? input.checked : true })
    .map(function (input) {
        return input.value;
    })

var quantityInput = event.currentTarget.querySelector('input[name=quantity]');

var variant = _product.variants.find(function (variant) {
    return options.every(function (option, index) {
        return variant['option' + (index + 1)] === option;
    });
});

fetch('/cart/add.js', {
    headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
    },
    method: 'post',
    body: JSON.stringify({ id: variant && variant.id, quantity: quantityInput && quantityInput.valueAsNumber || 1 })
}).then(function () {
    document.dispatchEvent(new CustomEvent('updateBagCount'));
})

